import "../assets/css/galeria.css";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';


import Heart from "./Heart";
import { useEffect, useState } from "react";


export default function Home() {

  const [info, setInfo] = useState([])
  const fotos = () => {
    fetch('/fotos.json')
      .then(response => response.json())
      .then(data => {
        setInfo(data.photos.map(info => info.src.original))
      });
  }
  useEffect(() => {
    fotos()
  }, [])

  return (
    <div className="galeria grid-columns-5 p-3 witdh-50% hight-50%">
      {
        info.map(original => {
          return <div> <Card style={{ width: '100%' }} {...{height: '100%'}}>
          <Card.Img variant="top"  key={original} src={original} alt="foto" className="" />
          <Card.Body>
            <Card.Title>Card Title</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <Button variant="primary">Go somewhere</Button>
          </Card.Body>
        </Card>
          </div>
        })
      }
    </div>
  );
}
